import React, {Component} from 'react';
import '../App.css';
// import '../bootstrap.css';
import '../main.a3f694c0.css';
import '../index.css';
import Navbar from './Navbar';
import ImgContainer from './ImgContainer';
import Contact from './Contact';
import Footer from './Footer';
import AboutSection from './AboutSection';
import IconSection from './IconSection'

// import axios from 'axios'

class App extends Component {
	constructor(){
		super();
		this.state={
			show: true,
			scrollPos: 0
		}
		this.handleScroll = this.handleScroll.bind(this);
		this.handleScrollTo = this.handleScrollTo.bind(this)
	}

	componentDidMount(){
		window.addEventListener("scroll", this.handleScroll);
		// window.addEventListener("click", this.handleScrollTo);

	}

	componentWillUnmount(){
		window.removeEventListener("scroll", this.handleScroll())
		console.log('EventListener unmounted')
	}

	handleScroll(){
		const {scrollPos} = this.state;
		this.setState({
      scrollPos: document.body.getBoundingClientRect().top,
      show: document.body.getBoundingClientRect().top > scrollPos
    });
	}

	handleScrollTo(event){
		var element = event.target.href;
		var elementTo = document.getElementById(element);
		element.scrollIntoView();
		console.log(element, elementTo)

	}

	render(){
		return(
			<div>
				<Navbar className={this.state.show ? "" : "hidden"}
					handleClick={this.handleScrollTo}/>
				<ImgContainer/>
				<AboutSection />
				<IconSection/>
				<Contact/>
				<Footer/>
			</div>
			)
	}

}

export default App;
