import React, {Component} from 'react';
import '../index.css';
// import '../bootstrap.css';
import '../main.a3f694c0.css';
import Navbar from './Navbar';
import ImgContainer from './ImgContainer';
import Contact from './Contact';
import Footer from './Footer';
import AboutSection from './AboutSection';
import IconSection from './IconSection';
import { Router, Route } from 'react-router';


// import axios from 'axios'

class App extends Component {
	constructor(){
		super();
		this.state={
			show: true,
			scrollPos: 0,
			showNav: false
		}
		this.handleScroll = this.handleScroll.bind(this);
		this.navButtonClick = this.navButtonClick.bind(this);
		this.removeHash = this.removeHash.bind(this)
	}



	componentDidMount(){
		window.addEventListener("scroll", this.handleScroll);
	}

	componentWillUnmount(){
		window.removeEventListener("scroll", this.handleScroll);
	}

	handleScroll(){
		const {scrollPos} = this.state;
		this.setState({
      scrollPos: document.body.getBoundingClientRect().top,
      show: document.body.getBoundingClientRect().top > scrollPos  || window.scrollY === 0
    });
    if(!this.state.show){
    	this.setState({showNav: false})
    }
	}

	navButtonClick(){
		this.setState({showNav: !this.state.showNav});
	}

	removeHash(click) {
	  const { hash } = window.location.href;
	  if (hash !== '') {
	    // Push onto callback queue so it runs after the DOM is updated,
	    // this is required when navigating from a different page so that
	    // the element is rendered on the page before trying to getElementById.
	    setTimeout(() => {
	      const id = hash.replace('#', '');
	      const element = document.getElementById(id);
	      if (element) element.scrollIntoView();
	    }, 0);
	  }
	}

	render(){
		return(
			<div>
				<Navbar 
					className={this.state.show ? "" : "hidden"}
					navClass={!this.state.showNav ? "collapse navbar-collapse" : "collapse navbar-collapse in"} 
					handleClick={this.navButtonClick}
					onClick={this.removeHash}
				/>
				<ImgContainer/>
				<AboutSection />
				<IconSection/>
				<Contact/>
				<Footer/>
			</div>

			)
	}

}

export default App;
