import React, {Component} from 'react';
import Button from '../components/Button';
import CheckBox from '../components/CheckBox';  
import Input from '../components/Input';  
import TextArea from '../components/TextArea';

// import axios from 'axios'

class FormContainer extends Component {
	constructor(props) {
		super(props);
		this.state = {
			field: {
				fname: '', 
				email: '', 
				message: ''				
			},
	  	checkBox: 'false',
	  	errors: {
	  		// nameError: '',
	  		// emailError: '',
	  		// messageError: ''
	  	}
		}

		this.handleInput = this.handleInput.bind(this);	
		this.handleFormSubmit = this.handleFormSubmit.bind(this);
		this.handleValidation = this.handleValidation.bind(this);
		this.checkBoxCheck = this.checkBoxCheck.bind(this)
  }

	handleInput(event){
		const {field} = {...this.state}
		const currentstate = field;
		const {name, value} = event.target ;
		currentstate[name] = value;
		this.setState({field: currentstate});
	}

	checkBoxCheck(event){
		console.log(event.target.checked)
		if(event.target.checked){
			this.setState({checkBox: true})
		} else {
			this.setState({checkBox: false})
		}
	}

	handleClearForm(){
		this.setState({
	  	field: {
			fname: '', 
			email: '', 
			message: ''
		},
  	checkBox: false,
  	errors: {
  		nameError: '',
  		emailError: '',
  		messageError: ''
  	}
		})
	}

	handleValidation(){
		let {field} = this.state.field;
		let {errors} = {};
		let formIsValid = true;

		//Name
    if(!field[fname]){
       formIsValid = false;
       errors["fname"] = "Cannot be empty";
    }

    if(typeof field["fname"] !== "undefined"){
       if(!field["fname"].match(/^[a-zA-Z]+$/)){
          formIsValid = false;
          errors["fname"] = "Only letters";
       }        
    }

    //Email
    if(!field["email"]){
       formIsValid = false;
       errors["email"] = "Cannot be empty";
    }

    if(typeof field["email"] !== "undefined"){
       let lastAtPos = field["email"].lastIndexOf('@');
       let lastDotPos = field["email"].lastIndexOf('.');

       if (!(lastAtPos < lastDotPos && lastAtPos > 0 && field["email"].indexOf('@@') === -1 && lastDotPos > 2 && (field["email"].length - lastDotPos) > 2)) {
          formIsValid = false;
          errors["email"] = "Email is not valid";
        }
    }

    //Message
    if(!field["message"]){
       formIsValid = false;
       errors["message"] = "Cannot be empty";
    }
    this.setState({errors: errors})
	}

	handleFormSubmit(e) {
    e.preventDefault();
    this.handleValidation();
    // const templateId = 'template_EwQhSiZR';
		e.target.className += " was-validated";
		console.log(this.state.checkBox)
   //  if(!window.form.checkValidity()){
   //  	this.setState({displayErrors: true})
   //  	console.log('errors', this.state.displayErrors)
   //  	return;
   //  } else {
			// this.setState({displayErrors: false})
   //  	console.log('errors', this.state.displayErrors)
   //  	this.handleClearForm();
   //  	return;
   //  }

  //   if(this.state.checkBox === false){
		// 	this.sendFeedback(templateId, {message_html: this.state.message, from_name: this.state.name, reply_to: this.state.email})
		// } else {
		// 	console.log('some bot tried to attack you')
		// }
  }

  sendFeedback (templateId, variables) {
		window.emailjs.send(
  	'cernojit_gmail_com', templateId,
  	variables
  	).then(res => {
    	console.log('Email successfully sent!')
  	})
  	// Handle errors here however you like, or use a React error boundary
  	.catch(err => console.error('Oh well, you failed. Here some thoughts on the error that occured:', err))
  }


  render(){
    return (
    	<form className="needs-validation" noValidate onSubmit={this.handleFormSubmit}>
    	<div className='col-md-6'>
			 <div className="template-example">
	        <h2 className="template-title-example">Máte Dotaz?</h2>
	        	<Input 
	        		type={"text"}
	        		name={"fname"}
	        		id={"inputName"}
	        		placeholder={"Jméno a příjmení"}
	        		onChange={this.handleInput}
	        		value={this.state.field.name}
	        		className={'form-control'}
	        		/>
						<span style={{color: "red"}}>{this.state.errors["fname"]}</span>
	        	<Input 
	        		type={"email"}
	        		name={"email"}
	        		id={"inputEmail"}
	        		placeholder={"Email"}
	        		onChange={this.handleInput}
	        		value={this.state.field.email}
	        		className={'form-control'}	        		
	        		/>
						<span style={{color: "red"}}>{this.state.errors.emailError}</span>
	        	<TextArea 
	        		className={'form-control'}
	        		placeholder={"Napište Vaší zprávu"}
	        		name={"message"}
	        		value={this.state.field.message}
	        		handleChange={this.handleInput}
	        		/>
	        	<CheckBox
	        		name={'checkBox'}
	        		value={this.state.checkBox}
	        		handleChange={this.checkBoxCheck}
	        		/>
	        	<Button
	        		/>
				</div>
      </div>
      </form>
    )

  }
}

export default FormContainer;
