import React, {Component} from 'react';
import CheckBox from './CheckBox';
import Button from './Button'

class Form extends Component {
	constructor(props) {
		super(props);
		this.state = {
			field: {
				fname: '', 
				email: '', 
				message: ''				
			},
	  	checkBox: false
		}
	}

	handleInput = () => {

	}


	render(){
    return (
    	<form className="w-100" >
	        <h3>Máte Dotaz?</h3>
        	<fieldset id="sign_up" class="ba b--transparent ph0 mh0">
						<input class="pa2 input-reset ba bg-transparent hover-white w-100" type="name" name="name"  id="name" placeholder="Jméno a Přijmení"/>

	        	<input 
	        		type={"email"}
	        		name={"email"}
	        		id={"inputEmail"}
	        		placeholder={"Email"}
	        		pattern={/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/}
	        		onChange={this.handleInput}
	        		value={this.state.field.email}
	        		className=""	        		
	        		/>
	        	<textArea 
	        		className=""
	        		placeholder={"Napište Vaší zprávu"}
	        		rows="2"
	        		type="text"
	        		name={"message"}
	        		value={this.state.field.message}
	        		handleChange={this.handleInput}

	        		/>
	        	<CheckBox
	        		name={'checkBox'}
	        		value={this.state.checkBox}
	        		handleChange={this.checkBoxCheck}
	        		/>

	        	<Button action={this.onButtonClick}
	        		/>
      		</fieldset>
      		<div id="hidden-before" className="hidden">
						<div className="alert alert-success " role="alert">Vaše zpráva byla úspěšně odeslána
						</div>
      		</div>
      </form>
    )

  }
}


export default Form;
