import React, {Component} from 'react';
import Button from '../components/Button';
import CheckBox from '../components/CheckBox';  
import Input from '../components/Input';  
import TextArea from '../components/TextArea' 

class FormContainer extends Component {
	constructor(props) {
	super(props);
	this.state = {
  	name: '',
  	email: '',
  	message: '',
  	checkBox: 'false'
	}
	this.handleSubmit = this.handleSubmit.bind(this);
	this.handleFullName = this.handleFullName.bind(this);
	this.handleEmail = this.handleEmail.bind(this);
	this.checkValidity = this.checkValidity.bind(this)
  }

	handleSubmit ( event ) {
		  event.preventDefault();
		  console.log(' btn click', this.input.value)
		}

	handleFullName(event){
		let value = event.target.value;
		this.setState({name: value});
		console.log('Email Box: ', this.state.name)

	}

	handleEmail(event){
		let value = event.target.value;
		this.setState({email: value});
		console.log('Name Box: ', event.target.value)
	}

	checkValidity(checkbox){
		console.log('Validity function' , this.state.checkBox)
		// let value = checkbox.target.value;
		if(checkbox.checked === true){
			console.log("checked")
		}
		// if (value==='true'){

		// 	this.setState({checkBox: "false"})

		// } else {
		// 	this.setState({checkBox: "true"})
		// }
		// this.setState({checkBox: event.target.value})
	}

  render(){
    return (
    	<div className='col-md-6'>
			 <div className="template-example">
	        <h2 className="template-title-example">Máte Dotaz?</h2>
	        	<Input 
	        		type={"Name"}
	        		id={"inputName"}
	        		placeholder={"Jméno a příjmení"}
	        		handleChange={this.handleFullName}
	        		value={this.state.name}
	        		/>
	        	<Input 
	        		type={"Email"}
	        		id={"inputEmail"}
	        		placeholder={"Email"}
	        		handleChange={this.handleEmail}
	        		value={this.state.email}
	        		/>
	        	<TextArea />
	        	<CheckBox
	        		name={'checkbox'}
	        		value={this.state.checkBox}
	        		handleChange={this.checkValidity(this)}

	        		/>
	        	<Button/>
				</div>
      </div>
    )

  }
}

export default FormContainer;
