import React, {Component} from 'react';
import CheckBox from './CheckBox';
import Button from './Button'

class Form extends Component {
	constructor(props) {
		super(props);
		this.state = {
			field: {
				fname: '', 
				email: '', 
				message: ''				
			},
	  	checkBox: false
		}
	}

	handleInput = (event) => {
		const {field} = {...this.state};
		const {name,value} = event.target;
		fied[name] = value;
		this.setState({field: field})
		console.log(field[name], value);
	}

	handleChange = () => {
		this.setState({checkBox: true})
		console.log(this.state.checkBox)
	}


	render(){
    return (
    	<form className="w-100" >
	        <h3>Máte Dotaz?</h3>
        	<fieldset id="sign_up" className="ba b--transparent ph0 mh0">
        		<div className="mt3">
							<input 
								className="pa3 input-reset ba hover-black border-box b--black-20 br2 w-100" 
								type="name" 
								name="name"  
								placeholder="Jméno a Přijmení"
								onInputChange={this.handleInput}
								/>
						</div>
						<div className="mt3">
	        		<input 
	        			className="pa3 input-reset ba hover-black border-box b--black-20 br1 w-100" 
	        			type="email" 
	        			name="email-address"  
	        			placeholder="Email"
								onInputChange={this.handleInput}
	        			/>
	        	</div>
	        	<div className="mt3">
	        	  <textarea id="message" className="pa3 db border-box hover-black w-100 ba b--black-20 br2 mb3" name="message" placeholder="Napište Vaší zprávu"></textarea>
	        	</div>
	        	<div className="mt3">
	        	 <label className="pa0 ma0 lh-copy f6 pointer hidden"><input type="checkbox" 
        			onChange = {this.handleChange}/> Remember me</label>
	        	</div>
	        	<Button action={this.onButtonClick}/>
      		</fieldset>
      		<div id="hidden-before" className="hidden">
						<div className="alert alert-success " role="alert">Vaše zpráva byla úspěšně odeslána
						</div>
      		</div>
      </form>
    )

  }
}


export default Form;
