import React, {Component} from 'react';
import '../App.css';
// import '../bootstrap.css';
import '../main.a3f694c0.css';
import '../index.css';
import Navbar from './Navbar';
import ImgContainer from './ImgContainer';
import Contact from './Contact';
import Footer from './Footer';
import AboutSection from './AboutSection';
import IconSection from './IconSection'

// import axios from 'axios'

class App extends Component {
	constructor(){
		super();
		this.state={
			show: true,
			scrollPos: 0,
			showNav: false
		}
		this.handleScroll = this.handleScroll.bind(this);
		this.navButtonClick = this.navButtonClick.bind(this);
	}

	componentDidMount(){
		window.addEventListener("scroll", this.handleScroll);
	}

	componentWillUnmount(){
		window.removeEventListener("scroll", this.handleScroll);
	}

	handleScroll(){
		const {scrollPos} = this.state;
		this.setState({
      scrollPos: document.body.getBoundingClientRect().top,
      show: document.body.getBoundingClientRect().top > scrollPos  || window.scrollY === 0
    });
	}

	navButtonClick(){
		if(!this.state.showNav){
			document.getElementById('navbar-collapse-uarr').className += ' in';
			this.setState({showNav: true})
			console.log('should add in className', document.getElementById('navbar-collapse-uarr'))
		} else {
			document.getElementById('navbar-collapse-uarr').className -= 'in';
			this.setState({showNav: false})
			console.log('should remove in className', document.getElementById('navbar-collapse-uarr'))
		}
	}

	render(){
		return(
			<div>
				<Navbar 
					className={this.state.show ? "" : "hidden"}
					handleClick={this.navButtonClick}
				/>
				<ImgContainer/>
				<AboutSection />
				<IconSection/>
				<Contact/>
				<Footer/>
			</div>
			)
	}

}

export default App;
