import React, {Component} from 'react';
import Button from '../components/Button';
import CheckBox from '../components/CheckBox';  
import Input from '../components/Input';  
import TextArea from '../components/TextArea';


// import axios from 'axios'

class FormContainer extends Component {
	constructor(props) {
		super(props);
		this.state = {
			field: {
				fname: '', 
				email: '', 
				message: ''				
			},
	  	checkBox: false
		}
	}

	handleInput = (event) => {
		const {field} = {...this.state}
		const currentstate = field;
		const {name, value} = event.target ;
		currentstate[name] = value;
		this.setState({field: currentstate});
	}

	checkBoxCheck = (event) => {
		if(event.target.checked){
			this.setState({checkBox: true})
		} else {
			this.setState({checkBox: false})
		}
	}

	handleClearForm = () => {
		this.setState({
  		field: {
			fname: '', 
			email: '', 
			message: ''
			},
  		checkBox: false
		})
	}

  onButtonClick = (event) => {
    event.preventDefault();
    event.target.className += " was-validated";
    var isValid = true;
    const elements = document.getElementsByClassName('form-control');
    Object.values(elements).forEach(obj => {
    	if(!obj.validity.valid){
    		isValid = false
    	}
    })

    if(isValid && !this.state.checkBox){
    	document.getElementById('hidden-after').className+="hidden";
    	document.getElementById('hidden-before').className -="hidden";
    	fetch('http://localhost:3000/', {
		  		method: 'post',
		  		headers: {'Content-Type': 'application/json'},
		  		body: JSON.stringify({
		  			fname: this.state.field.fname,
		  			email: this.state.field.email,
		  			message: this.state.field.message
		  		})
		  	})
		  	.then(response => response.json())
		  	.then(console.log('Email should be sent'))
		  	.then(this.handleClearForm())
		  	.catch(err => console.log(err))

    } else if (isValid && this.state.checkBox){
    	document.getElementById('hidden-after').className+="hidden";
    	document.getElementById('hidden-before').className -="hidden";
    	this.handleClearForm();
    } else {
    	alert('message not sent, please try again')
    }
  }

  render(){
    return (
    	<form className="needs-validation" noValidate >
    	<div className='w-100'>
			 <div className="template-example">
	        <h3>Máte Dotaz?</h3>
        	<div id="hidden-after" className="">
	        	<Input 
	        		type={"text"}
	        		name={"fname"}
	        		id={"inputName"}
	        		placeholder={"Jméno a příjmení"}
	        		onChange={this.handleInput}
	        		value={this.state.field.name}
	        		className={'form-control'}

	        		/>
	        	<Input 
	        		type={"email"}
	        		name={"email"}
	        		id={"inputEmail"}
	        		placeholder={"Email"}
	        		pattern={/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/}
	        		onChange={this.handleInput}
	        		value={this.state.field.email}
	        		className={'form-control'}	        		
	        		/>
	        	<TextArea 
	        		className={'form-control'}
	        		placeholder={"Napište Vaší zprávu"}
	        		name={"message"}
	        		value={this.state.field.message}
	        		handleChange={this.handleInput}
	        		/>
	        	<CheckBox
	        		name={'checkBox'}
	        		value={this.state.checkBox}
	        		handleChange={this.checkBoxCheck}
	        		/>
	        	<Button action={this.onButtonClick}
	        		/>
      		</div>
      		<div id="hidden-before" className="hidden">
						<div className="alert alert-success " role="alert">Vaše zpráva byla úspěšně odeslána
						</div>
      		</div>
				</div>
      </div>
      </form>
    )

  }
}

export default FormContainer;
