import React, {Component} from 'react';
import Button from '../components/Button';
import CheckBox from '../components/CheckBox';  
import Input from '../components/Input';  
import TextArea from '../components/TextArea';
import '../index.css'
// import axios from 'axios'

class FormContainer extends Component {
	constructor(props) {
	super(props);
	this.state = {
  	name: '',
  	email: '',
  	message: '',
  	checkBox: false,
  	// displayErrors: false,
  	nameError: false,
  	emailError: false,
  	messageError: false,
  	displayErrors: {name: false, email: false, textbox: false}
	}
	this.handleFullName = this.handleFullName.bind(this);
	this.handleEmail = this.handleEmail.bind(this);
	this.handleTextBox = this.handleTextBox.bind(this);
	// this.checkValidity = this.checkValidity.bind(this)
	this.handleFormSubmit = this.handleFormSubmit.bind(this);
	this.validateField = this.validateField.bind(this)
  }

	handleFullName(event){
		let value = event.target.value;
		this.validateField('name', {value});
		this.setState({name: value}) ;
	}

	handleEmail(event){
		let value = event.target.value;
		this.setState({email: value});
  }

	handleTextBox(event){
		let value = event.target.value;

		this.setState({message: value});
	}

	// checkBot(event){
	// 	if(event.target.checked === true){
	// 		this.setState({checkBox: true})
	// 	} else {
	// 		this.setState({checkBox: false})
	// 	}
	// }

	handleClearForm(){
		this.setState({
	  	name: '',
	  	email: '',
	  	message: '',
	  	checkBox: false
		})
	}

	handleFormSubmit(e) {
    e.preventDefault();
    const templateId = 'template_EwQhSiZR';
   //  if(!window.form.checkValidity()){
   //  	this.setState({displayErrors: true})
   //  	console.log('errors', this.state.displayErrors)
   //  	return;
   //  } else {
			// this.setState({displayErrors: false})
   //  	console.log('errors', this.state.displayErrors)
   //  	this.handleClearForm();
   //  	return;
   //  }

  //   if(this.state.checkBox === false){
		// 	this.sendFeedback(templateId, {message_html: this.state.message, from_name: this.state.name, reply_to: this.state.email})
		// } else {
		// 	console.log('some bot tried to attack you')
		// }
  }

  sendFeedback (templateId, variables) {
		window.emailjs.send(
  	'cernojit_gmail_com', templateId,
  	variables
  	).then(res => {
    	console.log('Email successfully sent!')
  	})
  	// Handle errors here however you like, or use a React error boundary
  	.catch(err => console.error('Oh well, you failed. Here some thoughts on the error that occured:', err))
  }

  validateField(fieldName, {value}){
		console.log('fieldName input: ', fieldName);
		console.log('fieldName input value: ', value);

  	switch (fieldName){
  		case 'name':
  			this.setState({nameError:  value.length > 1});
  			this.state.displayErrors.name =  this.state.nameError ? true : false;
  			break;
  		default:
  			break;
  	}

  }

  render(){
  	var {displayErrors} = this.state.displayErrors;
    return (
    	<form className="" noValidate onSubmit={this.handleFormSubmit}>
    	<div className='col-md-6'>
			 <div className="template-example">
	        <h2 className="template-title-example">Máte Dotaz?</h2>
	        	<Input 
	        		type={"text"}
	        		id={"inputName"}
	        		placeholder={"Jméno a příjmení"}
	        		handleChange={this.handleFullName}
	        		value={this.state.name}
	        		// className={'has-warning form-control '}

	        		/>
	        	<Input 
	        		type={"email"}
	        		id={"inputEmail"}
	        		placeholder={"Email"}
	        		handleChange={this.handleEmail}
	        		value={this.state.email}
	        		className={displayErrors ? 'form-control displayErrors' : 'form-control'}	        		
	        		/>
	        	<TextArea 
	        		placeholder={"Napište Vaší zprávu"}
	        		handleChange={this.handleTextBox}
	        		value={this.state.message}
	        		required
	        		/>
	        	<CheckBox
	        		name={'checkbox'}
	        		value={this.state.checkBox}
	        		handleChange={this.checkValidity}

	        		/>
	        	<Button
	        		// action={this.handleClearForm}
	        		/>
				</div>
      </div>
      </form>
    )

  }
}

export default FormContainer;
