import React, {Component} from 'react';
import Icon from '../../img/nav_icon.svg';
import './navbar.css';
import 'tachyons'

class NavbarTachyons extends Component {
	constructor (){
		super();
		this.state = {
			menuIsOpen: false
		}
	}

	closeMenu(){
	  document.getElementById('menu').classList.replace('left-50', 'left-100')
	  document.getElementById('bar-top').classList.replace('top-clicked', 'top')
	  document.getElementById('bar-middle').classList.replace('middle-clicked', 'middle')
	  document.getElementById('bar-bottom').classList.replace('bottom-clicked', 'bottom')	  
	  this.setState({menuIsOpen: false})
	}

	openMenu() {
	  document.getElementById('menu').classList.replace('left-100', 'left-50')
		document.getElementById('bar-top').classList.replace('top', 'top-clicked')
	  document.getElementById('bar-middle').classList.replace('middle', 'middle-clicked')
	  document.getElementById('bar-bottom').classList.replace('bottom', 'bottom-clicked')
	  this.setState({menuIsOpen: true})
	}

	handleClickMenu = (event) => {
		if(this.state.menuIsOpen === false){
			this.openMenu();
		} else {
			this.closeMenu()
		}
	}

	render(){
		return(
			<nav className="nav-tach dt dt--fixed w-100 border-box ph4 pt0 ph6-ns" >
				<a className="icon dtc v-mid link w-20 w-25-ns tc tl-ns mb2 mb0-ns" href="#menu" title="Home">
			    <img src={Icon} id="icon" className="dib br-100" alt="Lupínková"/>
			  </a>

			  <div id="menu" className="static-ns absolute mt0-ns pa0-ns left-100 db dtc-ns v-mid w-40 w-100-ns w-75-ns h-80 tr">
			    <a className="link tr tl-ns f8-ns db dib-ns mr4 mr4-ns mt4" href="#home" title="Home">Domů</a>
			    <a className="link tr tl-ns f8-ns db dib-ns mr4 mr4-ns" href="#info" title="About">Informace</a>
			    <a className="link tr tl-ns f8-ns db dib-ns mr4 mr0-ns mb3" href="#kontakt" title="Contact">Kontakt</a>
			  </div>
			  <a href="/#" id="menu-btn" className="dtc tr v-mid dn-ns w-20 pa0 pa0-ns tc" >
			  <button type="button" className="white ba0 bg-white" onClick={this.handleClickMenu}>
		      <span id="bar-top" className="icon-bar top"></span>
		      <span id="bar-middle" className="icon-bar middle"></span>
		      <span id="bar-bottom" className="icon-bar bottom"></span>
		      </button>
			  </a>
			</nav>
		)
	}

}

export default NavbarTachyons;
